import React, { useEffect, useRef } from 'react';
import * as ee from '@google/earthengine';

function MapComponent() {
    const apiKey = process.env.API_KEY;
  const mapRef = useRef(null);

  useEffect(() => {
    ee.initialize({ accessToken:apiKey });

    // Create a map instance
    const map = new ee.Map(mapRef.current, {
      center: [37.7749, -122.4194], // Initial center (San Francisco)
      zoom: 8,
    });

    // Load your greenhouse gas data (replace with your actual data)
    const greenhouseGasData = ee.ImageCollection('MODIS/MYD04_L2')
      .filterDate('2023-01-01', '2023-12-31')
      .select('AOT_550');

    // Create a color palette
    const colorPalette = ['blue', 'green', 'yellow', 'orange', 'red'];

    // Create a visualization parameters object
    const visualizationParams = {
      min: 0,
      max: 1000, // Adjust max value based on your data range
      palette: colorPalette,
    };

    // Add the data to the map
    map.addLayer(greenhouseGasData, visualizationParams, 'Greenhouse Gas Concentrations');
  }, []);

  return <div ref={mapRef} style={{ width: '100%', height: '800px' }} />;
}

export default MapComponent;
